package org.certificateservices.custom.c2x.etsits102941.v121.datastructs.trustlist

import org.certificateservices.custom.c2x.common.BaseStructSpec
import org.certificateservices.custom.c2x.etsits103097.v131.datastructs.cert.EtsiTs103097Certificate
import org.certificateservices.custom.c2x.etsits103097.v131.datastructs.cert.SingleEtsiTs103097CertificateSpec

/**
 * Unit test for RootCaEntry
 *
 * @author Philip Vendil, p.vendil@cgi.com
 */
class RootCaEntrySpec extends BaseStructSpec {

    EtsiTs103097Certificate selfsignedRootCa = SingleEtsiTs103097CertificateSpec.genCert()
    EtsiTs103097Certificate linkRootCaCertificate = SingleEtsiTs103097CertificateSpec.genCert()

    def "Verify that constructor and getters are correct and it is correctly encoded"(){
        when:
        RootCaEntry rce1 = new RootCaEntry(selfsignedRootCa, linkRootCaCertificate)
        then:
        serializeToHex(rce1) == ""
        when:
        RootCaEntry rce2 = deserializeFromHex(new RootCaEntry(), "")
        then:
        rce2.getSelfsignedRootCa() == selfsignedRootCa
        rce2.getLinkRootCaCertificate() == linkRootCaCertificate
        when:
        RootCaEntry rce3 = new RootCaEntry(selfsignedRootCa, null)
        then:
        serializeToHex(rce3) == ""
        when:
        RootCaEntry rce4 = deserializeFromHex(new RootCaEntry(), "")
        then:
        rce4.getSelfsignedRootCa() == selfsignedRootCa
        rce4.getLinkRootCaCertificate() == null
    }

    def "Verify toString()"(){
        expect:
        new RootCaEntry(selfsignedRootCa, linkRootCaCertificate).toString() == """RootCaEntry [
  selfsignedRootCa=[
    version=3
    type=explicit
    issuer=[self=sha256]
