package org.certificateservices.custom.c2x.etsits102941.v131.datastructs.trustlist

import org.certificateservices.custom.c2x.common.BaseStructSpec
import org.certificateservices.custom.c2x.etsits103097.v131.datastructs.cert.EtsiTs103097Certificate
import org.certificateservices.custom.c2x.etsits103097.v131.datastructs.cert.SingleEtsiTs103097CertificateSpec

/**
 * Unit test for TlmEntry
 *
 * @author Philip Vendil, p.vendil@cgi.com
 */
class TlmEntrySpec extends BaseStructSpec {

    EtsiTs103097Certificate selfSignedTLMCertificate = SingleEtsiTs103097CertificateSpec.genCert()
    EtsiTs103097Certificate linkTLMCertificate = SingleEtsiTs103097CertificateSpec.genCert()
    Url accessPoint = new Url("http://test.com")

    def "Verify that constructor and getters are correct and it is correctly encoded"(){
        when:
        TlmEntry te1 = new TlmEntry(selfSignedTLMCertificate, linkTLMCertificate,accessPoint)
        then:
        serializeToHex(te1) == ""
        when:
        TlmEntry te2 = deserializeFromHex(new TlmEntry(), "")
        then:
        te2.getSelfSignedTLMCertificate() == selfSignedTLMCertificate
        te2.getLinkTLMCertificate() == linkTLMCertificate
        te2.getAccessPoint() == accessPoint
        when:
        TlmEntry te3 = new TlmEntry(selfSignedTLMCertificate, null, accessPoint)
        then:
        serializeToHex(te3) == ""
        when:
        TlmEntry te4 = deserializeFromHex(new TlmEntry(), "")
        then:
        te4.getSelfSignedTLMCertificate() == selfSignedTLMCertificate
        te4.getLinkTLMCertificate() == null
        te4.getAccessPoint() == accessPoint
    }

    def "Verify toString()"(){
